/*
You are given a string, s, and a list of words, words, that are all of the same
length. Find all starting indices of substring(s) in s that is a concatenation
of each word in words exactly once and without any intervening characters.
*/
#include <iostream>
#include <vector>
#include <unordered_map>

using namespace std;

class Solution 
{
public:
    vector<int> findSubstring(string s, vector<string>& words) 
    {
        if (s.length() == 0) return vans;
        if (words.size() == 0) return vans;

        for(vector<string>::iterator k = words.begin(); k != words.end(); ++k)
        {
            ++umapwords[*k];
        }

        wordlen = words[0].length();
        for (int i = 0; i < s.length(); ++i)
        {
            if ((wordlen*words.size()) > (s.length()-i)) return vans;
            
            unordered_map<string, int> umapcmp = umapwords;
            int k = i;
            for ( ; k < (i+wordlen*words.size()); k+=wordlen)
            {
                if (umapcmp[s.substr(k, wordlen)]-- > 0)
                {
                    //cout<<"after match, ="<<umapcmp[s.substr(k, wordlen)]<<endl;
                }
                else
                {
                    break;
                }
            }
            if (k == (i+wordlen*words.size()))
            {
                vans.push_back(i);
            }
        }
        return vans;
    }
private:
    int wordlen = 0;
    vector<int> vans;
    unordered_map<string, int> umapwords;

};

